const searchUser=require("../../businessLayer/user/searchUser");
exports.checkUserId=(app)=>{
  app.get('/checkUserId.do',(req,res)=>{
    let isSend=false;
    res.setTimeout(3000,()=>{
      isSend=true;
      res.send({
        code:10000,
        data:null,
        msg:'连接超时',
        success:false
      });
    });
    searchUser.searchUser({userId:req.query.userId},(result)=>{
      if(result==='error'){
        (!isSend)&&res.send({
          code:10000,
          data:null,
          msg:'查询用户表发生错误',
          success:false
        })
      }else{
        (!isSend)&&res.send({
          code:10000,
          data:result,
          msg:'',
          success:true
        })
      }
    });
  })
};
